import React from "react";
import "./Bookingform.css";
import AttachmentIcon from "@mui/icons-material/Attachment";
//import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import { useForm } from "react-hook-form";

const Bookingform = () => {
  const { register, handleSubmit, errors } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <div className="booking-container">
      <div className="booking">
        {/* <div className="person">
          <AccountCircleIcon className="avatar-icon" />
          <h2>name</h2>
  </div> */}
        <form onSubmit={handleSubmit(onSubmit)} className="form-worker">
          <h3>Personal Details</h3>
          <div className="booking-form">
            <input

              type="text"
              placeholder="Full name"
              /* {...register("fullname", { required: true })} */
            />
            {errors.fullname && <span>Fullname field is required</span>}
            <input
              name="address"
              type="text"
              placeholder="Address"
              {...register("address", { required: true })}
            />
            {errors.address && <span>Address field is required</span>}
          </div>
          <h3>Booking Details</h3>
          <div className="booking-details">
            <div className="details-right">
              <input
                name="date"
                type="text"
                placeholder="Booking date"
                {...register("date", { required: true })}
              />
              {errors.date && <span>Date field is required</span>}
              <a className="attach">
                <AttachmentIcon className="attach-icon" />
                <div className="include">Include an image</div>
              </a>
            </div>
            <textarea
              name="description"
              placeholder="Description the problem"
              {...register("description", { required: true })}
            ></textarea>
            {errors.description && <span>description field is required</span>}
          </div>
          <div className="booking-btn">
            <button onClick={handleSubmit(onSubmit)} className="btn">
              Book
            </button>
            <button className="btn">Back</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Bookingform;
